def folderPath =  'C:/Users/n57977a/Desktop/CredMag1/' + testCase.name + '_' + System.currentTimeMillis() + File.separator
new File(folderPath).mkdirs()
    testCase.testSteps.each 
    { testStepName, testStep ->

        def response = testStep.getProperty('Response')?.getValue() 
        if(response)
        {
                            req(folderPath,testStep,testStepName)
            def file = new File(folderPath + testStepName + '_response.xml')
            file.write(response)
        }

    }


public req(folderPath,testStep,testStepName)
{
                        def request = testStep.getProperty('rawRequest')?.getValue()      

        if(request)
        {
            def file = new File(folderPath + testStepName + '_request.xml')
            file.write(request)
        }
     return;
}

